import React, { useState } from 'react';
import './ChessBoard.css';

const initialBoardSetup = [
  ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
  ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
  ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R']
];

const pieceMap = {
  'P': '♙', 'R': '♖', 'N': '♘', 'B': '♗', 'Q': '♕', 'K': '♔',
  'p': '♟', 'r': '♜', 'n': '♞', 'b': '♝', 'q': '♛', 'k': '♚'
};

const ChessBoard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [turn, setTurn] = useState('white'); // Track whose turn it is
  const [highlightedMoves, setHighlightedMoves] = useState([]);

  const isValidMove = (piece, startRow, startCol, endRow, endCol) => {
    const dx = Math.abs(endCol - startCol);
    const dy = endRow - startRow;

    if (board[endRow][endCol] && ((piece.toUpperCase() === piece && board[endRow][endCol].toUpperCase() === board[endRow][endCol]) || 
                                  (piece.toLowerCase() === piece && board[endRow][endCol].toLowerCase() === board[endRow][endCol]))) {
      return false; // Cannot move to a square occupied by the same color piece
    }

    switch (piece.toLowerCase()) {
      case 'p': // Pawn
        if (piece === 'P') { // White pawn
          if (startRow === 6 && endRow === 4 && dx === 0 && board[endRow][endCol] === '' && board[endRow + 1][endCol] === '') { // First move two squares
            return true;
          }
          if (startRow === 3 && endRow === 2 && Math.abs(startCol - endCol) === 1 && board[endRow][endCol] === '' && enPassant && enPassant.row === endRow && enPassant.col === endCol) { // En passant
            return true;
          }
          return (dy === -1 && dx === 0 && board[endRow][endCol] === '') || // Move forward
                 (dy === -1 && dx === 1 && board[endRow][endCol] !== ''); // Capture
        } else { // Black pawn
          if (startRow === 1 && endRow === 3 && dx === 0 && board[endRow][endCol] === '' && board[endRow - 1][endCol] === '') { // First move two squares
            return true;
          }
          if (startRow === 4 && endRow === 5 && Math.abs(startCol - endCol) === 1 && board[endRow][endCol] === '' && enPassant && enPassant.row === endRow && enPassant.col === endCol) { // En passant
            return true;
          }
          return (dy === 1 && dx === 0 && board[endRow][endCol] === '') || // Move forward
                 (dy === 1 && dx === 1 && board[endRow][endCol] !== ''); // Capture
        }
      case 'r': // Rook
        return (dx === 0 || Math.abs(dy) === 0) && !isPathBlocked(startRow, startCol, endRow, endCol);
      case 'n': // Knight
        return dx * Math.abs(dy) === 2;
      case 'b': // Bishop
        return dx === Math.abs(dy) && !isPathBlocked(startRow, startCol, endRow, endCol);
      case 'q': // Queen
        return (dx === 0 || Math.abs(dy) === 0 || dx === Math.abs(dy)) && !isPathBlocked(startRow, startCol, endRow, endCol);
      case 'k': // King
        return dx <= 1 && Math.abs(dy) <= 1;
      default:
        return false;
    }
  };

  const handleSquareClick = (row, col) => {
    if (selectedPiece) {
      const { piece, startRow, startCol } = selectedPiece;
      if (isValidMove(piece, startRow, startCol, row, col)) {
        const newBoard = board.map((r) => r.slice());
        newBoard[startRow][startCol] = '';
        newBoard[row][col] = piece;

        // Handle en passant capture
        if (piece.toLowerCase() === 'p' && enPassant && enPassant.row === row && enPassant.col === col) {
          newBoard[startRow][col] = '';
        }

        // Set en passant opportunities
        if (piece.toLowerCase() === 'p' && Math.abs(startRow - row) === 2) {
          setEnPassant({ row: (startRow + row) / 2, col });
        } else {
          setEnPassant(null);
        }

        setBoard(newBoard);
        setTurn(turn === 'white' ? 'black' : 'white'); // Change turn
      }
      setSelectedPiece(null);
      setHighlightedMoves([]);
    } else if (board[row][col] && ((turn === 'white' && board[row][col].toUpperCase() === board[row][col]) || (turn === 'black' && board[row][col].toLowerCase() === board[row][col]))) {
      setSelectedPiece({ piece: board[row][col], startRow: row, startCol: col });
      setHighlightedMoves(getPossibleMoves(board[row][col], row, col));
    }
  };


  const [enPassant, setEnPassant] = useState(null); // Track en passant opportunities


  const isPathBlocked = (startRow, startCol, endRow, endCol) => {
    const dx = endCol > startCol ? 1 : endCol < startCol ? -1 : 0;
    const dy = endRow > startRow ? 1 : endRow < startRow ? -1 : 0;
    let x = startCol + dx;
    let y = startRow + dy;

    while (x !== endCol || y !== endRow) {
      if (board[y][x] !== '') return true;
      x += dx;
      y += dy;
    }

    return false;
  };

  const getPossibleMoves = (piece, startRow, startCol) => {
    const moves = [];
    for (let row = 0; row < 8; row++) {
      for (let col = 0; col < 8; col++) {
        if (isValidMove(piece, startRow, startCol, row, col)) {
          moves.push({ row, col });
        }
      }
    }
    return moves;
  };


  return (
    <div className="game-container">
      <h2>{turn === 'white' ? 'White' : 'Black'}'s Turn</h2>
      <div className="chessboard">
        {board.map((row, rowIndex) => (
          <div key={rowIndex} className="row">
            {row.map((piece, colIndex) => {
              const isHighlighted = highlightedMoves.some(move => move.row === rowIndex && move.col === colIndex);
              return (
                <div
                  key={colIndex}
                  className={`square ${(rowIndex + colIndex) % 2 === 0 ? 'white' : 'black'} ${isHighlighted ? 'highlighted' : ''}`}
                  onClick={() => handleSquareClick(rowIndex, colIndex)}
                >
                  {piece && <span className={`piece ${piece.toUpperCase() === piece ? 'white-piece' : 'black-piece'}`}>{pieceMap[piece]}</span>}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChessBoard;
